package com.zlx.akoj;
import java.util.Scanner;

public class e1024 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner cin = new Scanner(System.in);
		int a = cin.nextInt();
		int b = cin.nextInt();
		while (a != 0 && b != 0){
			System.out.println(a + b);
		a = cin.nextInt();
		b = cin.nextInt();}
	}

}
