package com.zlx.nyoj;
import java.util.Scanner;

public class e112 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner cin = new Scanner(System.in);

		while (cin.hasNext()) {
			int X = cin.nextInt();
			int N = cin.nextInt();
			System.out.println((long) (Math.pow(X, N)));

		}

	}

}
